#read the Claims files and filter on the relevant years for yearly split / historical data 
from pyspark.sql.functions import to_date, year, month, dayofmonth, col;
dfClaims = spark.read.option("multiline", "false").option("columnNameOfCorruptRecord", "corrupt").schema (schemaClaims).json(claimsPath);

dfClaimsByYear = dfClaims.withColumn('year', year(to_date(dfClaims.created))).where(col('year') > 2015).where (col('year') < 2021) ;
#print(dfClaimsByYear.count())

dfClaimsByYear.write.option("multiline", "false").mode("overwrite").partitionBy ('year').json(outClaimsPath)

#read the Observations  files and filter on the relevant years for yearly split 
from pyspark.sql.functions import to_date, year, col;
dfObservations = spark.read.option("multiline", "false").option("columnNameOfCorruptRecord", "corrupt").schema(schemaObservations).json(observationPath);

dfObservationsByYear = dfObservations.withColumn('year', year(to_date(dfObservations.issued))).where(col('year') > 2015).where(col ('year') < 2021) 

dfObservationsByYear.write.option("multiline", "false").mode("overwrite").partitionBy ('year').json(outObservationsPath)
