#set up variables like file location 
dataPath = 'abfss://synapsehackaton@asadatalakeimre2.dfs.core.windows.net/30tb/*/';
claimsPath = dataPath + 'Claim.ndjson';
observationPath = dataPath + 'Observation.ndjson';

outClaimsPath = 'abfss://bronze@asadatalakeimre2.dfs.core.windows.net/historic_data/claims/'
outObservationsPath = 'abfss://bronze@asadatalakeimre2.dfs.core.windows.net/historic_data/Observation/'

#read the Claims files and filter on the relevant years for yearly split 
from pyspark.sql.functions import to_date, year, col;
dfObservations = spark.read.json(observationPath);

dfObservationsByYear = dfClaims.withColumn('year', year(to_date(dfClaims.created))).where((dfClaimsByYear.year > 2015) and (dfClaimsByYear.year < 2021)) 
#dfClaimsByYear.count()

dfObservationsByYear.write.option("mutiline", "false").partitionBy ('year').json(outObservationsPath)
